import React, { useState } from 'react';
import { Text, View, StyleSheet, TextInput, Button } from 'react-native';

const calculateDistance = (x, y, z) => {
  return Math.sqrt(x * x + y * y + z * z);
};

const Quiz2 = () => {
  const [x, setX] = useState('');
  const [y, setY] = useState('');
  const [z, setZ] = useState('');
  const [distance, setDistance] = useState(null);

  const handleCalculate = () => {
    const calculatedDistance = calculateDistance(parseFloat(x), parseFloat(y), parseFloat(z));
    setDistance(calculatedDistance.toFixed(2));
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Distance of (x, y, z) from (0, 0, 0)</Text>
      <Text>Write the code for this app which calculates</Text>
      <TextInput
        style={styles.input}
        placeholder="x"
        value={x}
        onChangeText={(text) => setX(text)}
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        placeholder="y"
        value={y}
        onChangeText={(text) => setY(text)}
        keyboardType="numeric"
      />
      <TextInput
        style={styles.input}
        placeholder="z"
        value={z}
        onChangeText={(text) => setZ(text)}
        keyboardType="numeric"
      />
      <Button title="CALCULATE DISTANCE" onPress={handleCalculate} />
      {distance && <Text>Distance: {distance}</Text>}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'flex-start',
    padding: 8,
    backgroundColor: '#ecf0f1',
    paddingTop: Platform.OS === 'android' ? 25 : 0,
  },
  title: {
    margin: 24,
    fontSize: 18,
    fontWeight: 'bold',
    textAlign: 'left',
  },
  input: {
    width: '100%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
});

export default Quiz2;
